Method.Implementation:
	set ret = $$$OK
	try {
		set testName=""
		$$$ThrowOnError(..ParseString(..%filterValues.TestName, .testName))

		// build SQL
		set tFields = "SELECT $PIECE(i.timecreated, ' ', 2), i.messages as ninput, o.messages as noutput "
		set tFrom = "FROM stats_data.inputbytime i full outer join stats_data.outputbytime o on i.testname=o.testname and i.timecreated=o.timecreated "
		set tWhere = ""
		set tGroupBy = ""

		if testName'="" {
			if $l(testName,",")>1 {
				set tWhere = tWhere_" WHERE i.TestName IN "_testName
			} else {
				set tWhere = tWhere_" WHERE i.TestName = '"_testName_"'"
			}
		}

   		set pSQL = tFields_" "_tFrom_" "_tWhere_" "_tGroupBy

   		set ^zdebug("KPI.InputOutputByTestName")=pSQL
	} catch ex {
		set ret = ex.AsStatus()
	}
   	quit ret
