Method.Implementation:
	set ret = $$$OK
	try {
		set testName=""
		$$$ThrowOnError(..ParseString(..%filterValues.TestName, .testName))

		// build SQL
		set tFields = "SELECT 'Total', SUM(ElapsedTime) TotalElapsedTime, CAST(AVG(ElapsedTime)/AVG(PercentElapsedTime) AS DEC(3,5)) AvgSessionTime "
		set tFrom = "FROM Stats_Data.OutputByPartner"
		set tWhere = ""
		set tGroupBy = ""

		if testName'="" {
			if $l(testName,",")>1 {
				set tWhere = tWhere_" WHERE TestName IN "_testName
			} else {
				set tWhere = tWhere_" WHERE TestName = '"_testName_"'"
			}
		}

   		set pSQL = tFields_" "_tFrom_" "_tWhere_" "_tGroupBy

   		set ^zdebug("KPI.InputTotalByTestName")=pSQL
	} catch ex {
		set ret = ex.AsStatus()
	}
   	quit ret
